mapper.select:
select id, username, login_ip, login_location, os, browser, status, login_type, login_time, create_time
from sys_login_log
where invalid = 0

    and username like concat('%', #{filter.username}, '%')


    and status = #{filter.status}


    and login_time >= #{filter.startTime}


    and login_time <= #{filter.endTime}

order by login_time desc
